import React, { useState, forwardRef, useRef } from "react";
import styles from "./mgupload.css";
import { uuids } from './utils/utils'
import upload from './public/svg/upload.svg';


function MgUpload(props, ref) {
  const {
    fileList,
    handleChange = () => { },
  } = props
  const [files, setFiles] = useState([]);
  const inputRef = useRef();

  const onChange = (info) => {
    const fileLists = [];
    Object.entries(info.currentTarget.files).forEach(item => {
      const [key, value] = item;
      const file = {
        uid: uuids(),
        name: value.name,
        size: value.size,
        type: value.type,
        lastModified: value.lastModified,
        lastModifiedDate: value.lastModifiedDate,
        originFileObj: item[1]
      };
      fileLists.push(file);
    });
    setFiles([...files, ...fileLists])
    handleChange({ fileList: fileLists });
  };

  const newFileList = fileList || files;
  return (
    <div className={styles.mgUpload}>
      <div>
        <input
          ref={inputRef}
          style={{ display: "none" }}
          type="file"
          files={newFileList}
          onChange={onChange}
          multiple={true}
        />
        <button onClick={() => { inputRef.current.click() }}
        >
          <span style={{ background: `url(${upload})`, width: 14, height: 14, display: 'inline-block' }}></span>上传
        </button>
        {newFileList.map(item => <div key={item.uid}>
          {item.name}
        </div>)}
        {
          newFileList.map(item => <img src={item.url || window.URL.createObjectURL(item.originFileObj)}></img>)
        }
      </div>
    </div>
  );
}

export default forwardRef(MgUpload);
