import React, { useState, forwardRef, useRef } from "react";
import "./styles.css";
// import { Upload, Button } from "antd";
// import { UploadOutlined } from "@ant-design/icons";
import { uuids } from './utils/utils'


function MgUpload(props, ref) {
  // const [fileList, setFileList] = useState([]);
  const [jsFileList, setJsFileList] = useState([]);
  const inputRef = useRef();
  // const uploadProps = {
  //   onChange: (info) => {
  //     console.log(info.file);
  //     console.log(info.fileList);
  //     setFileList(info.fileList);
  //   },
  //   beforeUpload: () => false,
  //   multiple: true,
  //   fileList: fileList
  // };

  const handleChange = (info) => {
    console.log(info);
    console.log(info.currentTarget.files);
    const fileList = [];
    Object.entries(info.currentTarget.files).forEach((item) => {
      const [key, value] = item;
      console.log(value)
      const file = {
        uid: uuids(),
        name: value.name,
        size: value.size,
        type: value.type,
        lastModified: value.lastModified,
        lastModifiedDate: value.lastModifiedDate,
        originFileObj: item[1]
      };
      fileList.push(file);
    });
    console.log(fileList);

    setJsFileList(fileList);
  };

  console.log(jsFileList)
  return (
    <div className="App">
      {/* <Upload {...uploadProps}>
        <Button>
          <UploadOutlined /> Click to Upload
        </Button>
      </Upload> */}
      <div>
        <input
          ref={inputRef}
          style={{ display: "none" }}
          type="file"
          files={jsFileList}
          onChange={handleChange}
          multiple={true}
        />
        <button onClick={() => { inputRef.current.click()}}
        >
          上传
        </button>
        {jsFileList.map(item => <div key={item.uid}>
          {item.name}
        </div>)}
        {
          jsFileList.map(item => <img src={item.url || window.URL.createObjectURL(item.originFileObj)}></img>)
        }
      </div>
    </div>
  );
}

export default forwardRef(MgUpload);
