import React, { useState, forwardRef, useRef } from "react";
import "./mgupload.css";
import { uuids } from './utils/utils'

function MgUpload(props, ref) {
  const {
    fileList,
    handleChange = () => { },
    children
  } = props
  const [files, setFiles] = useState([]);
  const inputRef = useRef();

  const onChange = (info) => {
    const fileLists = [];
    Object.entries(info.currentTarget.files).forEach(item => {
      const [key, value] = item;
      const file = {
        uid: uuids(),
        name: value.name,
        size: value.size,
        type: value.type,
        lastModified: value.lastModified,
        lastModifiedDate: value.lastModifiedDate,
        originFileObj: item[1]
      };
      fileLists.push(file);
    });
    setFiles([fileLists])
    handleChange({ fileList: fileLists });
  };

  const newFileList = fileList || files;
  return (
    <div className='mg-upload'>
      <input
        ref={inputRef}
        style={{ display: "none" }}
        type="file"
        files={newFileList}
        onChange={onChange}
        multiple={true}
      />
      {/* <div className='mg-upload-accessory'>
        <button className='mg-upload-button' onClick={() => { inputRef.current.click() }}>
          {children || <div className='mg-upload-button-content'>
            <span className='mg-upload-button-content-icon'></span>选择文件
          </div>
          }
        </button>
        {newFileList.map(item => <div className='mg-upload-accessory-item' key={item.uid}>
          <span className='mg-upload-accessory-item-icon'></span>{item.name}
        </div>)}
      </div> */}
      <div className='mg-upload-picture'>
        <div className='mg-upload-picture-select'>
          <div>
            <div>+</div>
            <div>选择图片</div>
          </div>
        </div>
      </div>
      {
        newFileList.map(item => <img key={item.uid} src={item.url || window.URL.createObjectURL(item.originFileObj)}></img>)
      }
    </div>
  );
}

export default forwardRef(MgUpload);
