import React, { useState, forwardRef, useRef } from "react";
import "./styles.css";
import { uuids } from './utils/utils'


function MgUpload(props, ref) {
  const {
    fileList,
    handleChange = () => { }
  } = props
  const [files, setFiles] = useState([]);
  const inputRef = useRef();

  const onChange = (info) => {
    const fileList = [];
    Object.entries(info.currentTarget.files).forEach(item => {
      const [key, value] = item;
      console.log(value)
      const file = {
        uid: uuids(),
        name: value.name,
        size: value.size,
        type: value.type,
        lastModified: value.lastModified,
        lastModifiedDate: value.lastModifiedDate,
        originFileObj: item[1]
      };
      fileList.push(file);
    });
    setFiles(fileList)
    handleChange({ fileList });
  };
  return (
    <div className="App">
      <div>
        <input
          ref={inputRef}
          style={{ display: "none" }}
          type="file"
          files={fileList || files}
          onChange={onChange}
          multiple={true}
        />
        <button onClick={() => { inputRef.current.click() }}
        >
          上传
        </button>
        {fileList || files.map(item => <div key={item.uid}>
          {item.name}
        </div>)}
        {
          fileList.map(item => <img src={item.url || window.URL.createObjectURL(item.originFileObj)}></img>)
        }
      </div>
    </div>
  );
}

export default forwardRef(MgUpload);
